// Psuedocode
// 1. At the start of the game there is a randomly generated number computer generated
// 2. Each crystal represents a number, but the number is hidden it is randomly generated
// 3. Each time you click on a crystal, the hidden number is displayed 
// 4. keep clicking different crystals till you get to the number generated by the computer 
// 5. If user click is equals to computer generated choice then display win. Also increase the win counter
// 6. If user click is greater than computer generated choice then display loss.
// 7. Continue to repeat until you are tired

// Global Variables

//Crystal Variables
var crystal = {
      crystalOne:
      {
        name: "Blue",
        value: 0
      },

      crystalTwo:
      {
        name: "Green",
        value: 0
      },

      crystalThree:
      {
        name: "Red",
        value: 0
      },

      crystalFour:
      {
        name: "Yellow",
        value: 0
      },

}

// //Scores

var currentScore = 0;
var targetScore = 0;

// Wins and Losses
var winCount = 0;
var lossCount = 0;



// // Functions
// Helper Function for getting random numbers
var getRandom = function(min, max){
  return Math.floor(Math.random()*(max-min+1)) + min;
}

// Starts the game and restarts the game
var startGame = function(){
  // reset the curent score
      var currentScore = 0;

  // set a new target score (between 19 and 120)
  targetScore = getRandom(19, 120);

  // set different values for each of the crystals
  crystal.crystalOne.value    = getRandom(1, 12);
  crystal.crystalTwo.value    = getRandom(1, 12);
  crystal.crystalThree.value  = getRandom(1, 12);
  crystal.crystalFour.value   = getRandom(1, 12);

  //change the html to reflect all of these changes 
  $("#yourScore").html(currentScore);
  $("#targetScore").html(targetScore);


  //console log for checking
  console.log("-------------------");
  console.log("Target Score: " + targetScore);
  console.log("Blue: " + crystal.crystalOne.value + " | Green: " + crystal.crystalTwo.value + " | Red: " 
    + crystal.crystalThree.value + " | Yellow: " + crystal.crystalFour.value);
  console.log("-------------------")
}

// Respond to clicks
var addValues = function(crystal){
  
  // Change current score
  currentScore = currentScore + crystal.value;

  // Change the HTML to reflect changes in currentScore
  $("#yourScore").html(currentScore);
  
  // Testing Console
  console.log("Your Score: " + currentScore);
}



//Main Process
//Starts the game the first time
startGame();

         $("#crystalOne").on("click", function(){ 
          addValues(crystal.crystalOne);
         
         });



       $("#crystalTwo").on("click", function(){ 
        addValues(crystal.crystalTwo);
        });



       $("#crystalThree").on("click", function(){ 
        addValues(crystal.crystalThree);
           
             
       });



       $("#crystalFour").on("click", function(){ 
        addValues(crystal.crystalFour);

      });
